<?php/** * The sidebar containing the left widget area. * * If no active widgets in sidebar, let's hide it completely. * * @package WordPress * @subpackage Transfers * @since Transfers 1.0 */global $exclude_left_sidebar_wrap;?><?php if ( is_active_sidebar( 'left' ) ) { ?>	<?php if ($exclude_left_sidebar_wrap) {		dynamic_sidebar( 'left' );	} else { ?>	<aside id="left-sidebar" class="left-sidebar widget-area one-fourth sidebar left" role="complementary">		<ul>		<?php dynamic_sidebar( 'left' ); ?>		</ul>	</aside><!-- #secondary -->	<?php } ?>	<?php wp_reset_postdata(); ?>	<?php }